import * as yup from "yup";
export const validate = yup.object({
  name: yup.string().max(20, "Less than 20 character").min(3, " At least 3 character and more").required("Name is required*"),
  mobile: yup.string().required("Mobile is required*").max(10, "must be 10 digits").min(10, "must be 10 digits"),
  email: yup.string().required("Email is required*").email("Invalid Email"),
  password: yup.string().required("Password is Required*").min(6, "minimum 6 character"),
  ConfirmPassword: yup.string().required("ConfirmPassword is required*").oneOf([yup.ref('password'), null], "password must be matched")
});
