import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import AccountBoxIcon from "@mui/icons-material/AccountBox";
import Card from "react-bootstrap/Card";
import Modal from 'react-bootstrap/Modal';
import { useState } from "react";
import {validate} from './signupSchema';
import {initialValues} from './signupModel'
import TextField from "./TextField";
import { Formik, Form } from "formik";



function Home() {
  const [show, setShow] = useState(false);
  const [formData, setFormData] = useState({});


  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Navbar bg="primary" expand="lg" variant="dark">
        <Container>
          <Navbar.Brand>Assessment</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto"></Nav>
            <Navbar.Brand
              style={{
                display: "flex",
                justifyContent: "space-around",
                width: "15rem",
              }}
            >
              <Button variant="primary" onClick={handleShow}>
                <AccountBoxIcon />
              </Button>
            </Navbar.Brand>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Card>
        <Card.Img
          variant="top"
          src="https://images.pexels.com/photos/1421903/pexels-photo-1421903.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
        />
      </Card>

      <Formik
        initialValues = {initialValues}
        validationSchema={validate}
        onSubmit={(values,action)=>{ 
          const {email,password} = values
          let user={
            email,
            password,
          } 
         setFormData(user)
         localStorage.setItem("user", JSON.stringify(user));
      // navigate(`/userdata`)


         action.resetForm()
        }     
      }
      >
        {(formik) => (
          // console.log(formik)
          <div style={{width:'50%',marginLeft:'10%'}}>
            <h1 style={{textAlign:'center'}}>SignUp</h1>
            <Form>

              <TextField label="Email" name="email" type="email" /><br></br>
              <TextField label="Password" name="password" type="password" /><br></br>

              <Button variant="primary" type="submit">
                SignIn
              </Button>
            </Form>
            <hr></hr>
          </div>
        )}
      </Formik>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>SignIN</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                autoFocus
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>password</Form.Label>
              <Form.Control
                type="password"  
                autoFocus
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>

          <Button variant="primary" onClick={handleClose}>
           SignIN
          </Button>
        </Modal.Footer>
      </Modal>

    </>
  );
}

export default Home;
