import React from "react";
import { Col, Button, Row, Container, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import TextField from "./TextField";
import { useState, useEffect } from "react";
import { validate } from "./signupSchema";
import { initialValues } from "./signupModel";
import { useNavigate } from "react-router-dom";
import { Formik, Form } from "formik";

function Register() {
  const [userData, setUserData] = useState([]);
  const navigate = useNavigate();
  // useEffect(() => {
  //   if (!localStorage.getItem("Reister-user")) {
  //     navigate("/register");
  //   }
  //   else{
  //     navigate('/profile')
  //   }
  // }, []);

  return (
    <div>
      <Formik
        initialValues={initialValues}
        validationSchema={validate}
        onSubmit={(values, action) => {

          const { name, mobile, email, password, ConfirmPassword } = values;
          let user = {
            name,
            mobile,
            email,
            password,
            ConfirmPassword,
            id: new Date().getTime().toString(),
          };
         // console.log(user)
          setUserData(user);
          let oldData = JSON.parse(localStorage.getItem('Reister-user'))
          clg
          localStorage.setItem("Reister-user", JSON.stringify([ ...oldData, ...user ]));
         // navigate(`/registerdUser`);

          action.resetForm();
        }}
      >
        {(formik) => (
          <Container>
            <Row className="vh-100 d-flex justify-content-center align-items-center">
              <Col md={8} lg={6} xs={12}>
                <Card className="px-4">
                  <Card.Body>
                    <div className="mb-3 mt-md-4">
                      <h2 className="fw-bold mb-2 text-center text-uppercase ">
                        Registeration
                      </h2>
                      <div className="mb-3">
                        <Form>
                          <TextField label="Name" name="name" type="text" />
                          <br></br>
                          <TextField label="Mobile" name="mobile" type="text" />
                          <br></br>
                          <TextField label="Email" name="email" type="text" />
                          <br></br>
                          <TextField
                            label="Password"
                            name="password"
                            type="password"
                          />
                          <br></br>
                          <TextField
                            label="ConfirmPassword"
                            name="ConfirmPassword"
                            type="password"
                          />
                          <br></br>
                          <Button variant="primary" type="submit">
                            Register
                          </Button>
                        </Form>
                        <div className="mt-3">
                          <p className="mb-0  text-center">
                            Already have an account? <Link to="/">Login</Link>
                          </p>
                        </div>
                      </div>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </Container>
        )}
      </Formik>
    </div>
  );
}

export default Register;
